import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { take, tap } from 'rxjs';
import { CarCard, SystemType } from 'src/app/interfaces/car-card.interface';
import { CarService } from 'src/app/services/car.service';

@Component({
  selector: 'app-all-vehicles',
  templateUrl: './all-vehicles.component.html',
  styleUrls: ['./all-vehicles.component.css']
})
export class AllVehiclesComponent {
  //test-test
  carCard: CarCard = {
    id: 1,
    model: 'Audi R8',
    review: 4.6,
    price: 2100,
    isAirConditioning: true,
    doorsNumber: 4,
    passangersNumber: 2,
    transmission: SystemType.Auto,
    reviewCount: 1345,
    image: '../../assets/imges/card/car1.png',

    make: 'Nissan',
    year: 2020,
    color: 'Black',
    mileage: 22000,
    fuelType: 'Gasoline',
    engine: '2.5L 4-cylinder',
    horsepower: 188,
    features: [
      'Remote Start',
      'Automatic Emergency Braking',
      'Lane Keeping Assist',
    ],
    owners: 1,
  };
  allCars: CarCard[] = [];
  carsPerPage: CarCard[] = [];
  filteredCars: CarCard[] = [];

  constructor(private carsService: CarService, private router: Router) {}
  ngOnInit(): void {
    this.getAllCars();
  }


  getFilterCar(searchValue:string): void {
    debugger
    if (searchValue) {
      const searchCarBtnValue = searchValue.toLowerCase().trim();
      this.filteredCars = this.allCars.filter(
        (car) =>
          car.make.toLowerCase().includes(searchCarBtnValue) ||
          car.model.toLowerCase().includes(searchCarBtnValue) || 
        searchCarBtnValue.includes(car.make.toLowerCase() && car.model.toLowerCase()) || 
        searchCarBtnValue.includes(car.model.toLowerCase())
      );
      this.carsPerPage = [...this.filteredCars].slice(0, 12);
    } else {
      this.filteredCars = [...this.allCars]
      this.carsPerPage = [...this.allCars].slice(0, 12);
    }
  }
  getAllCars(): void {
    this.carsService
      .getAllCars()
      .pipe(
        take(1),
        tap((res) => {
          this.allCars = res;
          this.carsPerPage = this.allCars.slice(0, 12);
          this.filteredCars = [...this.allCars]
        })
      )
      .subscribe();
  }
  navigateToHome(): void {
    this.router.navigateByUrl('/home');
  }

  displayItemAsperPagination(event: CarCard[]): void {
    this.carsPerPage = [...event];
  }
}


<div class="container pt-4">

    <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item " (click)="navigateToHome()"><a class="link-opacity-100-hover pointer">Home</a></li>
          <li class="breadcrumb-item active" aria-current="page">Cars</li>
        </ol>
      </nav>
</div>
<section class="container-lg sec-2 mt-4">
    <div class="d-flex justify-content-center m-4">
      <button class="popular-rental-deals-btn rounded-2">
        POPULAR RENTAL DEALS
      </button>
    </div>
  
    <h1 class="text-center m-4">Most popular cars rental deals</h1>
    <app-search-car (onFilterCar)="getFilterCar($event)"></app-search-car>

    <div class="d-flex flex-wrap justify-content-center car-card-container">
      <app-car-card *ngFor="let item of carsPerPage" [carCard]="item"></app-car-card>
    </div>
  
    <app-pagination [items]="filteredCars" (onPageChange)="displayItemAsperPagination($event)">

    </app-pagination>
  </section>
